sql.where:

h.intr_rate_type = #{intrRateType}
AND h.rsrv_type = #{rsrv_type}

AND h.intr_rate_min >= #{intrRateMin} 


AND h.intr_rate_max >= #{intrRateMax}

mapper.select:
SELECT t1.fin_prdt_cd, t1.fin_co_no, t1.kor_co_nm, t1.fin_prdt_nm, t1.join_way, t1.mtrt_int, t1.spcl_cnd, t1.etc_note, t1.intr_rate_min, t1.intr_rate_max, t1.intr_rate_type, t1.rsrv_type, t1.rsrv_type_nm, t1.intr_rate_type_nm t1.total_cnt, t1.rnum as no FROM ( SELECT h.*, ROW_NUMBER() OVER(ORDER BY h.fin_prdt_cd) as rnum, COUNT(*) OVER() as total_cnt FROM SU_SAVING h
)t1
WHERE t1.rnum >= (#{pageSize}*(#{pageNum}-1)+1) AND t1.rnum <= (#{pageSize}*(#{pageNum}-1)+#{pageSize})
